import { Component } from '@angular/core';
import { CommonModule } from '@angular/common';
import {MatTableModule} from '@angular/material/table';

export interface Users {
  username: string;
  position: number;
  age: number;
  symbol: string;
}

const ELEMENT_DATA: Users[] = [
  {position: 1, username: 'Steve', age: 1.0079, symbol: 'H'},
  {position: 2, username: 'Tony', age: 4.0026, symbol: 'He'},
  {position: 3, username: 'Natasha', age: 6.941, symbol: 'Li'},
  {position: 4, username: 'Wanda', age: 9.0122, symbol: 'Be'},
  {position: 5, username: 'Strange', age: 10.811, symbol: 'B'},
];

@Component({
  selector: 'app-task3',
  standalone: true,
  imports: [CommonModule, MatTableModule],
  templateUrl: './task3.component.html',
  styleUrls: ['./task3.component.scss']
})

export class Task3Component {
  displayedColumns: string[] = ['username', 'age', 'symbol'];
  dataSource = ELEMENT_DATA;
}
<div class="wrapper">
    <table mat-table [dataSource]="dataSource" class="mat-elevation-z8">

        <!--- Note that these columns can be defined in any order.
              The actual rendered columns are set as a property on the row definition" -->

        <!-- Position Column -->
        <!-- <ng-container matColumnDef="position">
          <th mat-header-cell *matHeaderCellDef> No. </th>
          <td mat-cell *matCellDef="let element"> {{element.position}} </td>
        </ng-container> -->

        <!-- username Column -->
        <ng-container matColumnDef="username">
          <th mat-header-cell *matHeaderCellDef> Username </th>
          <td mat-cell *matCellDef="let element"> {{element.username}} </td>
        </ng-container>

        <!-- age Column -->
        <ng-container matColumnDef="age">
          <th mat-header-cell *matHeaderCellDef> Age </th>
          <td mat-cell *matCellDef="let element"> {{element.age}} </td>
        </ng-container>

        <!-- Symbol Column -->
        <ng-container matColumnDef="symbol">
          <th mat-header-cell *matHeaderCellDef> Symbol </th>
          <td mat-cell *matCellDef="let element"> {{element.symbol}} </td>
        </ng-container>

        <tr mat-header-row *matHeaderRowDef="displayedColumns"></tr>
        <tr mat-row *matRowDef="let row; columns: displayedColumns;"></tr>
      </table>  
</div>
