import { Component } from '@angular/core';
import { CommonModule } from '@angular/common';
import { MatCardModule } from '@angular/material/card';
import { MatButtonModule } from '@angular/material/button';
import { FormsModule } from '@angular/forms';
import { SpecialDirective } from 'src/app/directives/special.directive';

@Component({
  selector: 'app-card',
  standalone: true,
  imports: [CommonModule, MatCardModule, MatButtonModule, FormsModule, SpecialDirective],
  templateUrl: './card.component.html',
  styleUrls: ['./card.component.scss'],
})
export class CardComponent {
  message: { greetings: string; port: number; status: string } = {
    greetings: 'Hola! Comos Estas?',
    port: 4200,
    status: 'Working | All Good',
  };

  getServerStatus(): string {
    return this.message.status;
  }

  // imageUrl: string = 'https://material.angular.io/assets/img/examples/shiba2.jpg';

  isLikeDisabled: boolean = false;
  isShareDisabled: boolean = true;
  toggle: boolean = false;

  count: number = 0;


  onLike(): void {
    this.toggle = !this.toggle;
    // this.message.status = this.toggle? "Working" : "Down";
    this.count +=1;
  }

  cars: string[] = ['Tesla', 'BYD', 'Tata']
}
<mat-card>
  <mat-card-header>
    <mat-card-title>{{ message.greetings }}</mat-card-title>
  </mat-card-header>
  <mat-card-content>
    <h3>I am running on port {{ message.port }}</h3>
    <p>Status: {{ getServerStatus() }}</p>
    <!-- <img [src]="imageUrl" alt="shiba"> -->
    <button
      [ngClass]="{'green' : toggle, 'red': !toggle}"
      mat-button
      [disabled]="isLikeDisabled"
      (click)="onLike()"
    >
      LIKE
    </button>
    <button mat-button [disabled]="isShareDisabled">SHARE</button>
  </mat-card-content>
  <h2>Likes {{ count }}</h2>
</mat-card>

<div class="wrapper">
  <input matInput [(ngModel)]="message.greetings">
  <!-- <h2 *ngIf="message.greetings === 'Cars' ">{{ message.greetings }} </h2> -->

  <div *ngIf="(message.greetings | lowercase) === 'cars' ">
    <div *ngFor="let car of cars">
      <p appSpecial>{{car}}</p>
    </div>
  </div>
</div>
