<div>
  <div>
    <p class="table-title">Adult Avengers</p>
  </div>
  <table>
    <tr>
      <th>Name</th>
      <th>Age</th>
      <th>Address</th>
    </tr>
    <ng-container *ngFor="let user of users">     
      <!-- ng-container does not renders in DOM -->
      <tr *ngIf="user.age>20">
        <td>{{user.username}}</td>
        <td>{{user.age}}</td>
        <td>{{user.address}}</td>
      </tr>
    </ng-container>
  </table>
</div>
