import { Component } from '@angular/core';
import { CommonModule } from '@angular/common';
import { SpecialDirective } from 'src/app/directives/special.directive';
import { FormsModule } from '@angular/forms';

@Component({
  selector: 'app-task2',
  standalone: true,
  imports: [CommonModule, FormsModule, SpecialDirective],
  templateUrl: './task2.component.html',
  styleUrls: ['./task2.component.scss']
})
export class Task2Component {
  message: { title: string, greetings: string; port: number; status: string } = {
    title: 'Enter Code to see car list',
    greetings: 'Enter Username',
    port: 4200,
    status: 'Working | All Good',
  };

  cars: string[] = ['Tesla', 'BYD', 'Tata'];


  size: string = '12 rem';
  style: string = 'font-size: 1.2rem; color: cornflowerblue;';
}
