import { Component } from '@angular/core';
import { CommonModule } from '@angular/common';
import {MatCardModule} from '@angular/material/card';

@Component({
  selector: 'app-card',
  standalone: true,
  imports: [CommonModule, CardComponent, MatCardModule],
  templateUrl: './card.component.html',
  styleUrls: ['./card.component.scss']
})
export class CardComponent {
    name: string = 'groot';
    contact: number = 985678;
}
<h3>Name: {{ name }}</h3>
<h4>Number: {{ contact }}</h4>
<p>card works!</p>

<mat-card>
    <mat-card-header>
      <mat-card-title>{{'GG'}}</mat-card-title>
    </mat-card-header>
    <mat-card-content>
      <p>I am running on port {{'port'}}</p>
    </mat-card-content>
  </mat-card>
