import { Component, EventEmitter, Input, Output } from '@angular/core';
import { CommonModule } from '@angular/common';
import { MatButtonModule } from '@angular/material/button';
import { MatCardModule } from '@angular/material/card';

@Component({
  selector: 'app-task4',
  standalone: true,
  imports: [CommonModule, MatCardModule, MatButtonModule],
  templateUrl: './task4.component.html',
  styleUrls: ['./task4.component.scss']
})
export class Task4Component {

  birthday = new Date(1988, 3, 15);

  salary: number = 10000; 

  inputFieldStyle: string = 'height: 10px; padding: 10px; margin: 0px 10px';

  // to recieve values from parent
  @Input({required: true}) titles: string[] = [];


  // to pass values to parent
  @Output() titleEvent$ = new EventEmitter<string>();

  detectInput(title: any): void {
    this.titleEvent$.emit(title);
    console.log(title);
  }

  // to pass values to parent to delete
  @Output() deleteEvent$ = new EventEmitter<string>();

  handleDelete(value: string): void{
    this.deleteEvent$.emit(value);
  }
}
<div class="card-wrapper">
  <ng-container *ngFor="let user of titles">
    <!-- <p>{{ user }}</p> -->
    <mat-card>
      <mat-card-header>
        <mat-card-title>{{ user | uppercase }}</mat-card-title>
      </mat-card-header>
      <mat-card-content>
        <p>Sa: {{ birthday | date:"MM/dd/yy" }} </p>
        <p>Birthday: {{ birthday | date:"MM/dd/yy" }} </p>
        <button mat-raised-button color="warn" (click)="handleDelete(user)">Delete</button>
      </mat-card-content>
    </mat-card>
  </ng-container>
</div>

<!-- detect and print immediately -->
<!-- <input type="text" (input)="detectInput($event)"> -->
<input [style]="inputFieldStyle" #username />
<button mat-raised-button color="primary" (click)="detectInput(username.value)">
  Press to add Users
</button>
