import { Component, Input } from '@angular/core';
import { CommonModule } from '@angular/common';
import { MatButtonModule } from '@angular/material/button';
import { MatCardModule } from '@angular/material/card';
import { Card } from './gcard.model';

@Component({
  selector: 'app-gcard',
  standalone: true,
  imports: [CommonModule, MatCardModule, MatButtonModule],
  templateUrl: './gcard.component.html',
  styleUrls: ['./gcard.component.scss']
})



export class GcardComponent {
  @Input() cards: Card[] = [];
}
<div class="card-container">
    <mat-card class="card" *ngFor="let card of cards">
      <mat-card-header>
        <mat-card-title>{{ card.name }}</mat-card-title>
        <mat-card-subtitle>{{ card.price }}</mat-card-subtitle>
      </mat-card-header>
      <img mat-card-image [src]="card.imageUrl" alt="card">
      <mat-card-content>
      </mat-card-content>
    </mat-card>
</div>
